package level1;

public class switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int month=3;
		switch(month) {
		case 1:
		{
			System.out.println("january");
			break;
		}
		case 2:
		{
			System.out.println("April");
			break;
		}
		case 3:
		{
			System.out.println("December");
			break;
		}
		default:
		{
			System.out.println("Nil.....");
		}
	}
	}
}
